import React, { useState } from "react";
import { Button, Form, ListGroup, Container, Row, Col } from "react-bootstrap";

function App() {
  const [posts, setPosts] = useState([]); // Lista de posts
  const [name, setName] = useState(""); // Nombre del nuevo post
  const [description, setDescription] = useState(""); // Descripción del nuevo post
  const [filter, setFilter] = useState(""); // Filtro de posts

  // Manejar el post nuevo
  const handleAddPost = () => {
    if (name.trim() && description.trim()) {
      const newPost = { name, description };
      setPosts([...posts, newPost]);
      setName(""); // Limpiar el campo de nombre
      setDescription(""); // Limpiar el campo de descripción
    } else {
      alert("Por favor, completa ambos campos antes de agregar un post.");
    }
  };

  // Manejar eliminar el último post
  const handleDeletePost = () => {
    setPosts(posts.slice(0, -1));
  };

  // Manejar el filtro
  const handleFilterChange = (e) => {
    setFilter(e.target.value);
  };

  // Filtrar posts
  const filteredPosts = posts.filter(
    (post) =>
      post.name.toLowerCase().includes(filter.toLowerCase()) ||
      post.description.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <Container className="mt-4">
      <Row>
        <Col>
          <h1 className="text-center">Gestión de Posts</h1>
        </Col>
      </Row>

      <Row className="my-3">
        <Col>
          <Form>
            <Form.Group className="mb-2">
              <Form.Label>Nombre del Post</Form.Label>
              <Form.Control
                type="text"
                placeholder="Ingresa el nombre"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Descripción del Post</Form.Label>
              <Form.Control
                as="textarea"
                rows={2}
                placeholder="Ingresa la descripción"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </Form.Group>

            <Button variant="primary" className="me-2" onClick={handleAddPost}>
              Crear Post
            </Button>
            <Button variant="danger" className="me-2" onClick={handleDeletePost}>
              Eliminar Último Post
            </Button>
          </Form>
        </Col>
      </Row>

      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            placeholder="Filtrar posts por nombre o descripción"
            value={filter}
            onChange={handleFilterChange}
          />
        </Col>
      </Row>

      <Row>
        <Col>
          <h5>Lista de Posts:</h5>
          {filteredPosts.length > 0 ? (
            <ListGroup>
              {filteredPosts.map((post, index) => (
                <ListGroup.Item key={index}>
                  <h6>{post.name}</h6>
                  <p>{post.description}</p>
                </ListGroup.Item>
              ))}
            </ListGroup>
          ) : (
            <p>No hay posts disponibles.</p>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
