import React, { useState } from "react";
import { Button, Form, ListGroup, Container, Row, Col } from "react-bootstrap";

function App() {
  const [posts, setPosts] = useState([]);
  const [filter, setFilter] = useState("");

  // Manejar el post nuevo
  const handleAddPost = () => {
    const newPost = `Post ${posts.length + 1}`;
    setPosts([...posts, newPost]);
  };

  // Manejar eliminar el último post
  const handleDeletePost = () => {
    setPosts(posts.slice(0, -1));
  };

  // Manejar el filtro
  const handleFilterChange = (e) => {
    setFilter(e.target.value);
  };

  // Filtrar posts
  const filteredPosts = posts.filter((post) =>
    post.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <Container className="mt-4">
      <Row>
        <Col>
          <h1 className="text-center">Gestión de Posts</h1>
        </Col>
      </Row>

      <Row className="my-3">
        <Col>
          <Button variant="primary" className="me-2" onClick={handleAddPost}>
            Crear Post
          </Button>
          <Button variant="danger" className="me-2" onClick={handleDeletePost}>
            Eliminar Último Post
          </Button>
        </Col>
      </Row>

      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            placeholder="Filtrar posts"
            value={filter}
            onChange={handleFilterChange}
          />
        </Col>
      </Row>

      <Row>
        <Col>
          <h5>Lista de Posts:</h5>
          {filteredPosts.length > 0 ? (
            <ListGroup>
              {filteredPosts.map((post, index) => (
                <ListGroup.Item key={index}>{post}</ListGroup.Item>
              ))}
            </ListGroup>
          ) : (
            <p>No hay posts disponibles.</p>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
